  return (
    <div className="home">
      <h1 className="home-title">Latest News</h1>
      <div className="news-grid">
        {newsData.map((item) => (
          <Link to={`/article/${item.id}`} key={item.id} className="news-card">
            <h2>{item.title}</h2>
            <p>{item.summary}</p>
            <span className="read-more">Read More â†’</span>
          </Link>
        ))}
      </div>
    </div>
  );
}
