import { useParams, Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import newsData from '../data/news';
import './ArticlePage.css';

export default function ArticlePage() {
  const { id } = useParams();
  const article = newsData.find((item) => item.id.toString() === id);

  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  // Загружаем комментарии из localStorage при открытии статьи
  useEffect(() => {
    const storedComments = localStorage.getItem(`comments_${id}`);
    if (storedComments) {
      setComments(JSON.parse(storedComments));
    }
  }, [id]);

  // Сохраняем комментарии в localStorage
  const saveComments = (updatedComments) => {
    localStorage.setItem(`comments_${id}`, JSON.stringify(updatedComments));
  };

  // Обработка отправки комментария
  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmed = newComment.trim();
    if (!trimmed) return;

    const newEntry = {
      text: trimmed,
      date: new Date().toLocaleString(),
    };

    const updatedComments = [...comments, newEntry];
    setComments(updatedComments);
    saveComments(updatedComments);
    setNewComment("");
  };

  if (!article) {
    return (
      <div className="article-page">
        <h2>Статья не найдена</h2>
        <Link to="/" className="back-button">← Вернуться на главную</Link>
      </div>
    );
  }

  return (
    <div className="article-page">
      <Link to="/" className="back-button">← Вернуться на главную</Link>
      <div className="article-content">
        <h1>{article.title}</h1>
        <p className="article-date">Опубликовано: {article.date || 'неизвестно'}</p>
        <p className="article-body">{article.content || 'Контент статьи скоро будет добавлен.'}</p>
      </div>

      <div className="comments-section">
        <h3>Комментарии</h3>
        <form onSubmit={handleSubmit} className="comment-form">
          <textarea
            rows="3"
            placeholder="Оставьте комментарий..."
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            required
          />
          <button type="submit">Добавить комментарий</button>
        </form>

        {comments.length === 0 ? (
          <p>Комментариев пока нет.</p>
        ) : (
          <ul className="comment-list">
            {comments.map((comment, index) => (
              <li key={index}>
                <p>{comment.text}</p>
                <span className="comment-date">{comment.date}</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
