import React from "react";
import { useParams, Link } from "react-router-dom";
import newsData from "../data/news";

export default function ArticlePage() {
  const { id } = useParams();
  const article = newsData.find((item) => item.id.toString() === id);

  if (!article) {
    return <div className="p-4">Статья не найдена.</div>;
  }

  return (
    <div className="max-w-3xl mx-auto p-4">
      <Link to="/" className="text-blue-600 underline">&larr; Назад</Link>
      <h1 className="text-3xl font-bold mt-4">{article.title}</h1>
      <p className="text-gray-600 text-sm mb-4">{article.date}</p>
      <p className="text-lg">{article.content}</p>
    </div>
  );
}
