import React, { useState } from "react";

const articles = [
  { id: 1, title: "Новые технологии в AI", category: "Технологии", text: "AI меняет мир..." },
  { id: 2, title: "Финансовый рынок растёт", category: "Бизнес", text: "Акции поднимаются..." },
  { id: 3, title: "Победа на чемпионате мира", category: "Спорт", text: "Сборная выиграла матч..." },
  { id: 4, title: "Учёные нашли воду на Марсе", category: "Наука", text: "Это изменит исследования..." },
];

const categories = ["Все", "Технологии", "Бизнес", "Спорт", "Наука"];

function Home() {
  const [selected, setSelected] = useState("Все");

  const filtered = selected === "Все"
    ? articles
    : articles.filter((a) => a.category === selected);

  return (
    <div>
      <h1>Главные новости</h1>

      <div className="category-filter">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelected(cat)}
            className={`cat-btn ${selected === cat ? "active" : ""}`}
          >
            {cat}
          </button>
        ))}
      </div>

      <div className="news-list">
        {filtered.map((article) => (
          <div className="news-card" key={article.id}>
            <h3>{article.title}</h3>
            <span className="category-tag">{article.category}</span>
            <p>{article.text}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
