import React, { useState } from "react";

const fakeArticles = [
  { id: 1, title: "Новые технологии в AI", category: "Технологии" },
  { id: 2, title: "Финансовый рынок растёт", category: "Бизнес" },
  { id: 3, title: "Победа на чемпионате мира", category: "Спорт" },
  { id: 4, title: "Учёные нашли воду на Марсе", category: "Наука" },
];

const categories = ["Все", "Технологии", "Бизнес", "Спорт", "Наука"];

function Categories() {
  const [selected, setSelected] = useState("Все");

  const filtered = selected === "Все"
    ? fakeArticles
    : fakeArticles.filter((a) => a.category === selected);

  return (
    <div>
      <h1>Категории</h1>
      <div style={{ marginBottom: 20 }}>
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelected(cat)}
            style={{
              marginRight: 10,
              padding: "6px 12px",
              background: selected === cat ? "#007bff" : "#eee",
              color: selected === cat ? "#fff" : "#333",
              border: "none",
              cursor: "pointer",
              borderRadius: 4,
            }}
          >
            {cat}
          </button>
        ))}
      </div>

      <ul>
        {filtered.map((article) => (
          <li key={article.id}>
            <strong>{article.category}</strong>: {article.title}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Categories;
