import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [selectedCategory, setSelectedCategory] = useState('');

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const news = [
    { title: 'Новость 1', description: 'Описание новости 1', category: 'Технологии', image: 'https://via.placeholder.com/300' },
    { title: 'Новость 2', description: 'Описание новости 2', category: 'Бизнес', image: 'https://via.placeholder.com/300' },
    { title: 'Новость 3', description: 'Описание новости 3', category: 'Здоровье', image: 'https://via.placeholder.com/300' },
    { title: 'Новость 4', description: 'Описание новости 4', category: 'Наука', image: 'https://via.placeholder.com/300' },
  ];

  const filteredNews = selectedCategory ? news.filter((item) => item.category === selectedCategory) : news;

  return (
    <div className="home-page">
      <div className="category-filter">
        <select onChange={handleCategoryChange} value={selectedCategory}>
          <option value="">Все категории</option>
          <option value="Технологии">Технологии</option>
          <option value="Бизнес">Бизнес</option>
          <option value="Здоровье">Здоровье</option>
          <option value="Наука">Наука</option>
        </select>
      </div>
      <div className="news-list">
        {filteredNews.map((newsItem, index) => (
          <div key={index} className="news-card">
            <img src={newsItem.image} alt={newsItem.title} />
            <div className="card-body">
              <h3>{newsItem.title}</h3>
              <p>{newsItem.description}</p>
            </div>
            <div className="card-footer">
              <Link to={`/news/${index}`}>Читать далее</Link>
            </div>
          </div>
        ))}
      </div>
      <footer className="footer">
        <p>Контактная информация</p>
        <p>© 2025 My News Site</p>
      </footer>
    </div>
  );
};

export default Home;
